import axios from "axios"; //api call from axios ajax for react

export default {
  getCard: function(q) { //TDD: return test first.
    //return { params: { q: "title:" + q } };
    return axios.get("/api/dandelion");//, { params: { q: "title:" + q} });
  },
  getSavedCard: function() {
    return axios.get("/api/card");  //return {}  //TDD: return test first. 
  },
  deleteCard: function(id) {
    return axios.delete("/api/card/" + id);  //return {} 
  },
  saveCard: function(cardData) {
    return axios.post("/api/card", cardData);  //return {} 
  }
};
